// Extract the full response from Zoho WorkDrive API node
const rawDataNode = $node["List all Zoho file details"].json;

// Debugging: Log the raw response from Zoho WorkDrive API
console.log("ðŸ“Œ Raw Zoho API Response:", rawDataNode);

// Ensure rawDataNode exists
if (!rawDataNode || !rawDataNode.data) {
    throw new Error("âŒ Zoho WorkDrive API response is empty or missing.");
}

// Identify where 'data' is located in the API response
let rawData;
if (Array.isArray(rawDataNode.data) && rawDataNode.data.length > 0) {
    rawData = rawDataNode.data[0]?.data; // First element in the array
} else if (typeof rawDataNode.data === "string") {
    rawData = rawDataNode.data; // If 'data' is a string
} else {
    throw new Error("âŒ Zoho WorkDrive API 'data' field is missing or undefined.");
}

// Debugging: Log extracted rawData
console.log("ðŸ“Œ Extracted rawData:", rawData);

// Check if rawData is a string and parse it
let parsedData;
if (typeof rawData === "string") {
    try {
        parsedData = JSON.parse(rawData);
    } catch (error) {
        throw new Error("âŒ Error parsing Zoho API response: " + error.message);
    }
} else {
    parsedData = rawData;
}

// Debugging: Log parsed data
console.log("ðŸ“Œ Parsed Data:", parsedData);

// Extract the file list safely
const zohoFiles = parsedData?.data || [];

// Ensure zohoFiles is an array
if (!Array.isArray(zohoFiles)) {
    throw new Error("âŒ Expected an array of files but received: " + JSON.stringify(zohoFiles));
}

// Debugging: Log final extracted file list
console.log("ðŸ“Œ Zoho Files List:", zohoFiles);

// Convert the Zoho file objects into a proper JSON format
const formattedFiles = zohoFiles.map(file => {
    let modifiedTime = file.attributes?.modified_time_in_millisecond || null;
    let createdTime = file.attributes?.created_time_in_millisecond || null;

    // Convert timestamps to full date format if available
    if (modifiedTime) {
        console.log("ðŸ” Original modified_time_in_millisecond:", modifiedTime);
        modifiedTime = new Date(modifiedTime).toISOString(); // Ensures correct year
    }

    if (createdTime) {
        console.log("ðŸ” Original created_time_in_millisecond:", createdTime);
        createdTime = new Date(createdTime).toISOString();
    }

    return {
        id: file.id,
        name: file.attributes?.name || "Unknown",
        createdTime: createdTime,  // âœ… Added created time
        lastModifiedTime: modifiedTime,
        download_url: file.attributes?.download_url || "",
        modifiedBy: file.attributes?.modified_by || "Unknown"
    };
});

// Debugging: Log formatted files before returning
console.log("ðŸ“Œ Formatted Files:", formattedFiles);

// Return the properly formatted array of file objects
return formattedFiles;

