return (() => {
    // Get all file IDs and names from "get file id and name for modified files" node
    const fileDataArray = $('get file id and name for modified files').all(); 

    // Get all file binaries from "Split into multiple file items" node
    const fileBinaries = $('Split into multiple file items').all();

    // Extract target filenames from fileDataArray
    const targetFileNames = fileDataArray.map(file => file.json.file_name);

    // Filter files that match any filename in targetFileNames
